// ScreenTurnRatioContext.js
import React, { createContext, useState, useContext } from 'react';

// Create the context
const TurnFactorContext = createContext();

// Custom hook to use the context
export function useTurnFactor() {
  return useContext(TurnFactorContext);
}

// Provider component
export function TurnFactorProvider({ children }) {
  const [turnFactor, setTurnFactor] = useState(0.1);

  return (
    <TurnFactorContext.Provider value={{ turnFactor, setTurnFactor }}>
      {children}
    </TurnFactorContext.Provider>
  );
}
