import { BIRD_COLOR, visibleRangeDistance } from "./constants"

// between two birds
const squaredDistance = function (b1, b2) { 
    return (b1.x - b2.x) ** 2 + (b1.y - b2.y) ** 2
}

export function nearbyBirds(current, all, distance) {
    return new Set ([...all].filter(b => 
        (b !== current && squaredDistance(current, b) <= distance ** 2)
        )    
    )
}

class Point {
    constructor(
        x,
        y
    ) {
        this.x = x
        this.y = y
    }

    rotate(angle) {

        // Calculate the rotated coordinates
        const rotatedX = this.x * Math.cos(angle) - this.y * Math.sin(angle);
        const rotatedY = this.x * Math.sin(angle) + this.y * Math.cos(angle);

        return new Point(rotatedX, rotatedY);
    } 

    transpose(dx, dy) {
        return new Point(this.x + dx, this.y + dy)
    }
}


// angle is bearing to elongated vertex in radians
export function drawTriangle(x, y, angle, ctx) {

    let vertex = [new Point(0, 3), new Point(-0.5, 0), new Point(0.5, 0)]
    vertex.forEach(p => {
        p.rotate(angle)
        p.transpose(x, y)
    });

    ctx.beginPath()
    ctx.moveTo(vertex[0])
    ctx.lineTo(vertex[1])
    ctx.lineTo(vertex[2])
    ctx.closePath()

    ctx.fillStyle = BIRD_COLOR
    ctx.fill()
}
