import React, { useEffect, useState, useRef } from 'react';
import './App.css';
import { Bird } from './Bird.js';
import { INITIAL_TURN_FACTOR, BACKGROUND_COLOR, BIRDS_NUM, CANVAS_HEIGHT, CANVAS_WIDTH, PROTECTED_RANGE_DISTANCE, visibleRangeDistance } from './constants';
import { nearbyBirds, drawTriangle, drawCircle } from './utilities.js';


function SliderComponent({ turnFactor, setTurnFactor }) {


  return (
    <div className="sliders">
      <label>
        Screen Turn:
        <input
          type="range"
          min="0"
          max="10"
          step="0.05"
          value={turnFactor}
          onChange={(e) => setTurnFactor(parseFloat(e.target.value))}
        />
        {turnFactor.toFixed(1)}
      </label>
    </div>
  );
}

function App() {
  const [turnFactor, setTurnFactor] = useState(INITIAL_TURN_FACTOR)

  // Initialize birds
  const initialBirds = Array.from({ length: BIRDS_NUM }, () => 
    new Bird(
      Math.random() * CANVAS_WIDTH,
      Math.random() * CANVAS_HEIGHT,
      Math.random() * 100 - 15, // Adjusted to include negative velocities
      Math.random() * 100 - 15  // Adjusted to include negative velocities
    )
  );

  const [birds, setBirds] = useState(initialBirds);
  const canvasRef = useRef(null);
  const prevTimestamp = useRef(0);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    if (!ctx) {
      console.error('Failed to get canvas context');  
      return;
    }

    const updateFrame = (timestamp) => {
      if (!prevTimestamp.current) {
        prevTimestamp.current = timestamp;
      }

      const deltaTime = (timestamp - prevTimestamp.current) / 1000; // Convert to seconds
      prevTimestamp.current = timestamp;

      // Update birds' states
      const updatedBirds = birds.map(bird => {
        let inProtectedRange = nearbyBirds(bird, birds, PROTECTED_RANGE_DISTANCE)
        let inVisibleRange = nearbyBirds(bird, birds, visibleRangeDistance);
        bird.separate(inProtectedRange);
        bird.align(inVisibleRange);
        bird.cohere(inVisibleRange);
        bird.screenTurn(turnFactor);
        return bird;
      });

      // Clear the canvas and redraw birds
      ctx.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT)
      ctx.fillStyle = BACKGROUND_COLOR
      ctx.fillRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT)

      updatedBirds.forEach(bird => {
        bird.update(deltaTime)
        // drawTriangle(bird.x, bird.y, Math.atan(bird.velY, bird.velX), ctx)
        drawCircle(bird.x, bird.y, ctx)

      });

      // Update the state with the new birds' positions
      setBirds(updatedBirds);

      // Request the next frame
      requestAnimationFrame(updateFrame);
    };

    // Start the animation loop
    requestAnimationFrame(updateFrame);

    // Cleanup function
    return () => {
      prevTimestamp.current = 0; // Reset timestamp on cleanup
    };
  }, []); // Empty array to ensure this effect runs only once

  return (
    <div className="App">
      <h1>Birds Flocking Simulation</h1>
      <div className="canvas-wrapper">
        <canvas className="canvas"
          ref={canvasRef}
          width={CANVAS_WIDTH}
          height={CANVAS_HEIGHT}
          style={{ display: 'block' }}
        />
      </div>
      <div className="sliders">
        <SliderComponent turnFactor={turnFactor} setTurnFactor={setTurnFactor}/>

      </div>
    </div>
  );
}

export default App;
